pragma solidity ^0.5.0;

contract AuctionBox {
    Auction[] public auctions;
    function createAuction(
        string memory _title,
        uint256 _startingPrice,
        string memory _description,
        uint256 _auctionLength
    ) public {
        require(_startingPrice > 0);
        // set the new instanc
        Auction newAuction = new Auction(
            msg.sender,
            _auctionLength,
            _title,
            _description,
            _startingPrice
        );
        // push the auction address to auctions array
        auctions.push(newAuction);
    }

    function returnAllAuctions() public view returns (Auction[] memory) {
        return auctions;
    }
}
contract Auction {
    // Parameters of the auction. Times are either
    // absolute unix timestamps (seconds since 1970-01-01)
    // or time periods in seconds.
    address payable public beneficiary;
    uint256 public auctionStart;
    uint256 public biddingTime;
    uint256 public startingPrice;
    string title;
    string description;
    // Current state of the auction.
    address public highestBidder;
    uint256 public highestBid;

    // Allowed withdrawals of previous bids
    mapping(address => uint256) pendingReturns;

    // Set to true at the end, disallows any change
    bool ended;

    // Events that will be fired on changes.
    event HighestBidIncreased(address bidder, uint256 amount);
    event AuctionEnded(address winner, uint256 amount);

    // The following is a so-called natspec comment,
    // recognizable by the three slashes.
    // It will be shown when the user is asked to
    // confirm a transaction.

    /// Create a simple auction with `_auctionLength`
    /// seconds bidding time on behalf of the
    /// beneficiary address `_beneficiary`.
    constructor(
        address payable _beneficiary,
        uint256 _auctionLength,
        string memory _title,
        string memory _description,
        uint256 _startingPrice
    ) public {
        title = _title;
        description = _description;
        beneficiary = _beneficiary;
        auctionStart = now;
        biddingTime = _auctionLength;
        startingPrice = _startingPrice;
    }

    /// Bid on the auction with the value sent
    /// together with this transaction.
    /// The value will only be refunded if the
    /// auction is not won.
    function bid() public payable {
        // No arguments are necessary, all
        // information is already part of
        // the transaction. The keyword payable
        // is required for the function to
        // be able to receive Ether.
        if (now > auctionStart + biddingTime) {
            // Revert the call if the bidding
            // period is over.
            revert();
        }
        if (msg.value <= highestBid) {
            // If the bid is not higher, send the
            // money back.
            revert();
        }
        if (highestBidder != address(0)) {
            // Sending back the money by simply using
            // highestBidder.send(highestBid) is a security risk
            // because it can be prevented by the caller by e.g.
            // raising the call stack to 1023. It is always safer
            // to let the recipient withdraw their money themselves.
            pendingReturns[highestBidder] += highestBid;
        }
        highestBidder = msg.sender;
        highestBid = msg.value;
        emit HighestBidIncreased(msg.sender, msg.value);
    }

    /// Withdraw a bid that was overbid.
    function withdraw() public returns (bool) {
        uint256 amount = pendingReturns[msg.sender];
        if (amount > 0) {
            // It is important to set this to zero because the recipient
            // can call this function again as part of the receiving call
            // before `send` returns.
            pendingReturns[msg.sender] = 0;

            if (!msg.sender.send(amount)) {
                // No need to call throw here, just reset the amount owing
                pendingReturns[msg.sender] = amount;
                return false;
            }
        }
        return true;
    }

    /// End the auction and send the highest bid
    /// to the beneficiary.
    function auctionEnd() public {
        // It is a good guideline to structure functions that interact
        // with other contracts (i.e. they call functions or send Ether)
        // into three phases:
        // 1. checking conditions
        // 2. performing actions (potentially changing conditions)
        // 3. interacting with other contracts
        // If these phases are mixed up, the other contract could call
        // back into the current contract and modify the state or cause
        // effects (ether payout) to be perfromed multiple times.
        // If functions called internally include interaction with external
        // contracts, they also have to be considered interaction with
        // external contracts.

        // 1. Conditions
        if (now <= auctionStart + biddingTime) revert(); // auction did not yet end
        if (ended) revert(); // this function has already been called

        // 2. Effects
        ended = true;
        emit AuctionEnded(highestBidder, highestBid);

        // 3. Interaction
        // if (!beneficiary.send(highestBid))
        //   revert();
        beneficiary.transfer(highestBid);
    }

    function returnContents() public view returns(
        string memory,
        uint256,
        string memory,
        uint256,
        uint256
        ) {
        return (
            title,
            startingPrice,
            description,
            auctionStart,
            biddingTime
        );
    }
}

